mapper.select:
SELECT COUNT(*)
FROM qa
WHERE user_id = #{user_id};

mapper.update:
UPDATE qa
SET title = #{title}, content = #{content}, email = #{email},
    tele_num = #{tele_num}, phon_num = #{phon_num}, img1 = #{img1},
    img2 = #{img2}, img3 = #{img3}
WHERE user_id = #{user_id}
AND qa_num = #{qa_num};

mapper.select:
SELECT *
FROM qa_state
WHERE qa_num = #{qa_num}
AND qa_stat_seq = #{qa_stat_seq};

mapper.update:
UPDATE qa_state
SET qa_stat_code = #{qa_stat_code},
    name = #{name},
    appl_begin = #{appl_begin},
    appl_end = now(),
    up_date = now(),
    up_id = #{up_id}
WHERE qa_num = #{qa_num}
AND qa_stat_seq = #{qa_stat_seq};

mapper.delete:
DELETE FROM qa_state
WHERE qa_num = #{qa_num}
AND qa_stat_seq = #{qa_stat_seq};
