mapper.select:
select count(*)
from qa
where user_id = #{user_id};

mapper.update:
update qa
set title = #{title}, content = #{content}, email = #{email},
    tele_num = #{tele_num}, phon_num = #{phon_num}, img1 = #{img1},
    img2 = #{img2}, img3 = #{img3}
where user_id = #{user_id}  and qa_num = #{qa_num};

mapper.delete:
delete
from qa
where user_id = #{user_id}
and qa_num = #{qa_num};
