mapper.select:
SELECT COUNT(*)
FROM qa
WHERE user_id = #{user_id};

mapper.update:
UPDATE qa
SET title = #{title}, content = #{content}, email = #{email},
    tele_num = #{tele_num}, phon_num = #{phon_num}, img1 = #{img1},
    img2 = #{img2}, img3 = #{img3}
WHERE user_id = #{user_id}
AND qa_num = #{qa_num};
